import { createTheme } from '@mui/material';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#81d4fa',
      light: '#b6ffff',
      dark: '#4ba3c7',
      contrastText: '#000',
    },
    secondary: {
      main: '#ff80ab',
      light: '#ffb2dd',
      dark: '#c94f7c',
      contrastText: '#000',
    },
    background: {
      default: '#0A1929',
      paper: '#132F4C',
    },
    text: {
      primary: '#81d4fa',
      secondary: 'rgba(129, 212, 250, 0.7)',
    },
    divider: 'rgba(129, 212, 250, 0.12)',
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
      color: '#81d4fa',
      marginBottom: '1rem',
      textShadow: '0 0 10px rgba(129, 212, 250, 0.3)',
    },
    subtitle1: {
      fontSize: '1.1rem',
      color: 'rgba(129, 212, 250, 0.7)',
      marginBottom: '2rem',
    },
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          padding: '2rem',
          borderRadius: '12px',
          backgroundColor: 'rgba(19, 47, 76, 0.9)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(129, 212, 250, 0.1)',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: '8px',
          padding: '10px 24px',
          textTransform: 'none',
          fontSize: '1rem',
        },
        contained: {
          background: 'linear-gradient(45deg, #81d4fa 30%, #4ba3c7 90%)',
          boxShadow: '0 3px 5px rgba(129, 212, 250, 0.3)',
          '&:hover': {
            background: 'linear-gradient(45deg, #4ba3c7 30%, #81d4fa 90%)',
          },
        },
        outlined: {
          borderColor: '#81d4fa',
          color: '#81d4fa',
          '&:hover': {
            borderColor: '#b6ffff',
            color: '#b6ffff',
          },
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-root': {
            borderRadius: '8px',
            backgroundColor: 'rgba(129, 212, 250, 0.05)',
            '&:hover': {
              backgroundColor: 'rgba(129, 212, 250, 0.08)',
            },
            '&.Mui-focused': {
              backgroundColor: 'rgba(129, 212, 250, 0.08)',
            },
          },
        },
      },
    },
    MuiStepLabel: {
      styleOverrides: {
        label: {
          color: 'rgba(129, 212, 250, 0.7)',
          '&.Mui-active': {
            color: '#81d4fa',
          },
          '&.Mui-completed': {
            color: '#4caf50',
          },
        },
      },
    },
  },
});

export default theme;
